<script>
	import DraggableDiv from '../../general/draggable_div/DraggableDiv.vue';
	import APIHandler from '../../ApiHandler';
	export default {
		name: 'DialogCreatePrintQRSetting',
		components: {
			DraggableDiv,
		},
		data() {
			return {
				api_handler: new APIHandler(window.Laravel.access_token),
				name: '',
				print_config: {
					dimension: {
						width: '5cm',
						height: '3cm',
					},
					DO: {
						top: '0cm',
						left: '0cm',
					},
					SO: {
						top: '0cm',
						left: '0cm',
					},
					qr_code: {
						top: '0cm',
						left: '0cm',
					},
				},
			};
		},
		methods: {
			async createNewSetting() {
				try {
					const { data } = await this.api_handler.post(
						'api/partner/deliveries/print-configs',
						{},
						{
							name: this.name,
							config: JSON.stringify(this.print_config),
						},
					);
					let print_config = data;
					print_config.config = JSON.parse(print_config.config);

					this.$emit('onPrintQRSettingCreated', print_config);
					this.$showMessage('success', 'Thêm thành công');
					this.closeDialog();
					this.resetForm();
				} catch (error) {
					console.log(error);
					this.$showMessage('error', 'Lỗi', error);
				}
			},
			resetForm() {
				this.name = '';
				this.print_config = {
					dimension: {
						width: '5cm',
						height: '3cm',
					},
					DO: {
						top: '0cm',
						left: '0cm',
					},
					SO: {
						top: '0cm',
						left: '0cm',
					},
					qr_code: {
						top: '0cm',
						left: '0cm',
					},
				};
			},
			closeDialog() {
				$('#DialogCreatePrintQRSetting').modal('hide');
			},
		},
	};
</script>

